<?php
AutoLoader::Import('library.mail.*');

class UtilityMailfixed extends AdminCommand
{

  public static $MailName = 'therunet12-2';

  /**
   * Основные действия комманды
   * @return void
   */
  protected function doExecute()
  {
    set_time_limit(84600);

    /* Шаблон сообщений */
    $this->mail = new View();
    $this->mail->SetTemplate(self::$MailName, 'utility', 'templates', 'mail');

    $fp = fopen(self::$MailName.'.log',"a+");
    $j=0;

    //$emails = $this->getEmails();
//    $emails = array(
//      array('nikitin@internetmediaholding.com', '_Nikitin'),
//      array('borzov@raec.ru', 'Borzov Maxim')
//    );
    foreach ($emails as $info)
    {
      $this->sendMail($info, false);

      if ($j == 300) { sleep(1); $j = 0; }; $j++;
      fwrite($fp, "{$info[0]}\n");
    }

    fclose($fp);
  }

  /**
   * @param array $info
   * @param bool $isHTML
   * @return void
   */
    private function sendMail($info, $isHTML = true)
    {
      $this->mail->Name = $info[1];
      $this->mail->ShowName = !(strlen($info[1]) == 0 || substr($info[1], 0, 1) == '_');

      $mail = new PHPMailer(false);
      $mail->ParamOdq = true;
      $mail->ContentType = ($isHTML) ? 'text/html' : 'text/plain';
      $mail->IsHTML($isHTML);
      $email = $info[0];
      if (!filter_var($email, FILTER_VALIDATE_EMAIL))
      {
        return;
      }
      $mail->AddAddress($email);
      $mail->SetFrom('info@therunet.com', 'TheRunet', false);
      $mail->CharSet = 'utf-8';
      $mail->Subject = '=?UTF-8?B?'. base64_encode('Russian Internet industry 2012 report is now available') .'?=';
      $mail->Body = $this->mail;

      //$mail->Send();
      echo $this->mail;
    }





