                                        }
                                    } else {
                                        $has = in_array($foodProductMatrix[$d][$i], $userFoodProductIdList);
                                    }
                                    ?>
                                    <?=$has ? '+' : '&nbsp;';?>
                                </td>
                            <?else:?>
                                <td colspan="2">&mdash;</td>
                            <?endif;?>
                        <?endfor;?>
                    </tr>
                <?endfor;?>
                </tbody>
            </table>
