  <body bgcolor="#000000" style="margin:0;padding:0;font-family:Verdana, Geneva, sans-serif;font-size:100%;line-height:1.6;background:#000000;-webkit-font-smoothing:antialiased;-webkit-text-size-adjust:none;height:100%;width:100% !important;">
    <!-- body -->
    <table class="body-wrap" style="margin:0;font-family:Verdana, Geneva, sans-serif;font-size:100%;line-height:1.6;width:100%;padding:0;border-collapse:collapse;">
      <tr style="margin:0;padding:0;font-family:Verdana, Geneva, sans-serif;font-size:100%;line-height:1.6;">
        <td style="margin:0;padding:0;font-family:Verdana, Geneva, sans-serif;font-size:100%;line-height:1.6;padding: 0;"></td>
        <td class="container" bgcolor="#000000" border="0" style="margin:0;padding:0;font-family:Verdana, Geneva, sans-serif;font-size:100%;line-height:1.6;padding:20px;box-sizing:border-box;display:block !important;max-width:600px !important;margin:0 auto !important;clear:both !important;border: 0; padding: 0!important;" align="center">
          <!-- header -->
          <img src="http://vizh.share.s3.amazonaws.com/ruvents/premia-runeta/20161031-premia(05)-top.jpg" style="margin:0;padding:0;font-family:Verdana, Geneva, sans-serif;font-size:100%;line-height:1.6;height:auto;max-width:100%;height: auto; margin: 0; width: 100%;"/>
