          <!-- header -->
          <img src="http://vizh.share.s3.amazonaws.com/ruvents/premia-runeta/20161107-premia-top.jpg" style="margin:0;padding:0;font-family:Verdana, Geneva, sans-serif;font-size:100%;line-height:1.6;height:auto;max-width:100%;height: auto; margin: 0; width: 100%;"/>
	    </td>
